import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './App.css';

const App = () => {
    const [input, setInput] = useState('')
    const [countries, setCountries] = useState([])

    const handleChange = (e) => {
        setInput(e.target.value)
    }

    useEffect(() => {
        axios.get(`https://restcountries.eu/rest/v2/all`)
            .then((response) => {
                setCountries(response.data.filter((country) => country.name.toLowerCase().indexOf(input) !== -1))
            })
    }, [input])
    return (
        <div className="App">
            <label>find countries</label>
            <input type="text" name="country" onChange={handleChange}></input>
            {countries.length < 10 && countries.length > 1 &&
            <ul>{countries.map((country) => {
                return <li key={country.name}>{country.name}</li>
            })}</ul>}
            {countries.length >= 10 && <p>Too many results</p>}
            {countries.length === 1 && 
                <div>
                    <h1>{countries[0].name}</h1>
                    <p>capital {countries[0].capital}</p>
                    <p>population {countries[0].population}</p>
                    <h2>languages</h2>
                    <ul>
                        {countries[0].languages.map((lang) => <li key={lang.name}>{lang.name}</li>)}
                    </ul>
                    <img style={{width: '100px'}} src={countries[0].flag}></img>
                </div>
            }

        </div>
    );
}

export default App;
