import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './App.css';

const App = () => {
    const [input, setInput] = useState('')
    const [countries, setCountries] = useState([])

    const handleChange = (e) => {
        setInput(e.target.value)
        setCountries(countries.filter((country) => {
            return country.name.toLowerCase().indexOf(input) !== -1
        }))
    }

    useEffect(() => {
        axios.get(`https://restcountries.eu/rest/v2/all`)
            .then((response) => {
                console.log(response.data)
                setCountries(response.data)
            })
    }, [])
    return (
        <div className="App">
            <label>find countries</label>
            <input type="text" name="country" onChange={handleChange}></input>
            {countries.length < 10 && 
            <ul>{countries.map((country) => {
                return <li key={country.name}>{country.name}</li>
            })}</ul>}
            {countries.length >= 10 && <p>Too many results</p>}


        </div>
    );
}

export default App;
